import React, { Component } from "react";
import Counter from "./counter";

class Counters extends Component {
  state = {
    counters: [
      { id: 1, value: 0 },
      { id: 2, value: 0 },
      { id: 3, value: 0 },
      { id: 4, value: 0 },
    ],
  };

  /* note that the event handler is in the parent 
component as it is the one that owns the piece of the counter state */
  handleDelete = (counterID) => {
    const counters = this.state.counters.filter((c) => c.id !== counterID);
    this.setState({
      counters: counters,
    }); /* note the use of colons rather than equal sign*/
  };

  render() {
    return (
      <div>
        {this.state.counters.map((counter) => (
          <Counter
            key={
              counter.id
            } /*note that key is read by React but we cant assess it, we need id to change state */
            value={counter.value}
            onDelete={this.handleDelete}
            id={counter.id}
          >
            <h4>Counter #{counter.id}</h4>
          </Counter>
        ))}
      </div>
    );
  }
}

export default Counters;
